import { createCamera } from './components/camera.js';
import { createCylinder } from './components/cylinder.js';
import { createScene } from './components/scene.js';
import { createRod } from './components/rod.js'

import { createRenderer } from './systems/renderer.js';
import * as THREE from 'three';
// import { create } from 'core-js/core/object';
// import { Resizer } from './systems/Resizer.js';

// These variables are module-scoped: we cannot access them
// from outside the module
let camera;
let renderer;
let scene;

class gyroscopeAnimation {
  constructor(container, parameters, state) {
    this.parameters = parameters
    this.state = state
    this.solution = {}

    // Create necessary elements, set up renderer, add to container
    camera = createCamera(container, this.parameters);
    scene = createScene();

    renderer = createRenderer();
    renderer.setSize(container.clientWidth, container.clientHeight)
    renderer.setPixelRatio(window.devicePixelRatio)

    container.append(renderer.domElement);

    // Add elements
    this.cylinder = createCylinder(parameters, state);
    [this.verticies, this.line] = createRod(parameters, this.getCartesian())
    this.axesHelper = new THREE.AxesHelper(3/2 * this.parameters.l.value)

    // Adjust their positions, initialize oldPosition vector which we will need to perform rotations.
    this.cylinder.position.set(0,0,this.parameters.l.value)
    this.oldPosition = new THREE.Vector3()
    this.cylinder.getWorldPosition(this.oldPosition)


    // Update cylinder position and orientation
    this.render()
    this.updateCylinder()
    scene.add(this.cylinder, this.line, this.axesHelper);

    // const resizer = new Resizer(container, camera, renderer);
  }

  render() { // used to render a single frame
    renderer.render(scene, camera);
  }

  updateSolution(solution){
    this.solution = solution
    this.startAnimation()
  }

  updateParameters(parameters){
    this.stopAnimation()
    this.parameters = parameters
    const geometry = new THREE.CylinderGeometry(
      this.parameters.a.value, // top radius
      this.parameters.a.value, // bottom radius
      this.parameters.h.value, // height
      32
      ); 

    this.cylinder.geometry = geometry
    this.updateCylinder(false)

    this.render()
  }

  updateState(state){
    this.stopAnimation()
    this.state = state

    this.updateCylinder()
    this.updateRod()
    this.render()
  }

  getCartesian(){ // return cartesian coordinates from theta, phi, and psi
    let x = this.parameters.l.value * Math.cos(this.state.theta.value) * Math.sin(this.state.phi.value)
    let y = this.parameters.l.value * Math.sin(this.state.theta.value) * Math.sin(this.state.phi.value)
    let z = this.parameters.l.value * Math.cos(this.state.phi.value)

    return {x:x, y:y, z:z}
  }

  updateCylinder(rotate = true){
    let cartesian = this.getCartesian()

    if (rotate == false) {
      this.cylinder.position.set(cartesian['x'], cartesian['y'], cartesian['z'])
      return
    }


    let currentPosition = new THREE.Vector3(cartesian['x'], cartesian['y'], cartesian['z'])

    let angle = this.oldPosition.angleTo(currentPosition)
    let difference = currentPosition.clone().sub(this.oldPosition)
    let rotationVector = new THREE.Vector3().crossVectors(difference, this.oldPosition)

    // theta and phi rotations
    this.cylinder.rotateOnWorldAxis(rotationVector.normalize(), -angle)

    // psi rotation
    this.cylinder.rotateOnWorldAxis(currentPosition.normalize(), this.state.psi.value)

    // set oldPosition so the next rotation can be calculated properly
    this.oldPosition = currentPosition.clone()
  }

  updateRod(){
    let cartesian = this.getCartesian()
    this.verticies[3] = cartesian['x'] * (this.parameters.l.max / this.parameters.l.value)
    this.verticies[4] = cartesian['y'] * (this.parameters.l.max / this.parameters.l.value)
    this.verticies[5] = cartesian['z'] * (this.parameters.l.max / this.parameters.l.value)
    this.line.geometry.setAttribute('position', new THREE.BufferAttribute(this.verticies, 3))
  }

  updateStateFromSolution(i){
    this.state.theta = this.solution.theta[i]
    this.state.phi = this.solution.phi[i]
    this.state.psi = this.solution.psi[i]
  }

  startAnimation() {
    let i = 1
    let length = this.solution.t.length
    renderer.setAnimationLoop(() => {
      this.updateStateFromSolution(i % length)
      this.updateCylinder()
      this.updateRod()
      i = i + 1
      renderer.render(scene, camera)
    })
  }

  stopAnimation() {
    renderer.setAnimationLoop(null)
  }
}

export { gyroscopeAnimation };
