import { SpotLight, AmbientLight } from 'three';

function createLights(parameters) {
    let spotLight = new SpotLight( 0xffffff , 1, 0, Math.PI/5, 0);
        spotLight.position.set( 0, 0, parameters.l.max * 2);
        spotLight.castShadow = true 
        spotLight.shadow.mapSize.width = 1024
        spotLight.shadow.mapSize.height = 1024
        spotLight.shadow.camera.near = 0.5
        spotLight.shadow.camera.far = 100

    let spotLight2 = new SpotLight( 0xffffff , 1, 0, Math.PI/5, 0);
        spotLight.position.set( 0, 0, -parameters.l.max * 1.1);
        spotLight.castShadow = true 
        spotLight.shadow.mapSize.width = 1024
        spotLight.shadow.mapSize.height = 1024
        spotLight.shadow.camera.near = 0.5
        spotLight.shadow.camera.far = 100

    return [spotLight, spotLight2];
}

export { createLights }
