import * as THREE from 'three';

class Rod {
    constructor(cartesian, parameters){
    this.parameters = parameters
    this.verticies = new Float32Array([0,0,0, cartesian['x']* (parameters.l.max / parameters.l.value), cartesian['y']* (parameters.l.max / parameters.l.value), cartesian['z']* (parameters.l.max / parameters.l.value)])
    this.lineMaterial = new THREE.LineBasicMaterial( { color: 'black' , linewidth : 1} );
    this.lineGeometry = new THREE.BufferGeometry()
    this.rod = new THREE.Line( this.lineGeometry, this.LineMaterial );

    this.rod.geometry.setAttribute('position', new THREE.BufferAttribute(this.verticies, 3))
    }

    get object() {
        return this.rod
    }

    tick(cartesian, parameters = this.parameters){
        this.parameters = parameters
        this.verticies[3] = cartesian['x'] * (this.parameters.l.max / this.parameters.l.value)
        this.verticies[4] = cartesian['y'] * (this.parameters.l.max / this.parameters.l.value)
        this.verticies[5] = cartesian['z'] * (this.parameters.l.max / this.parameters.l.value)
        this.rod.geometry.setAttribute('position', new THREE.BufferAttribute(this.verticies, 3))
    }
}

export { Rod };
