import * as THREE from 'three';

function createCylinder(parameters) {
  // create a geometry
  const geometry = new THREE.CylinderGeometry(
    parameters.a.value, // top radius
    parameters.a.value, // bottom radius
    parameters.h.value, // height
    32
    ); 
  const material = new THREE.MeshBasicMaterial({color : "red"})
  // const material = [new THREE.MeshPhongMaterial({ color: 'blue', shininess: 200 }),new THREE.MeshPhongMaterial({ color: 'red', shininess: 200 })]

  const cylinder = new THREE.Mesh(geometry, material);
  // cylinder.rotation.set(Math.PI/2,0,0)
  cylinder.position.set(0, 0, parameters.l.value)

  return cylinder;
}

export { createCylinder };
