import * as THREE from 'three';

function createCylinder(parameters) {
  // create a geometry
  const geometryCylinder = new THREE.CylinderGeometry(
    parameters.a.value, // top radius
    parameters.a.value, // bottom radius
    parameters.h.value, // height
    32
    ); 
  const cylinder = new THREE.Mesh(geometryCylinder, [new THREE.MeshPhongMaterial({ color: 'blue', shininess: 200 }),new THREE.MeshPhongMaterial({ color: 'red', shininess: 200 })]);
  cylinder.rotation.set(Math.PI/2,0,0)
  cylinder.position.set(0, 0, this.parameters.l)

  return cylinder;
}

export { createCylinder };
