import { DirectionalLight } from 'three';

function createLights(parameters) {
    // const spotLight = new SpotLight( 0xffffff , 1, 0, Math.PI/4);
    // spotLight.position.set( 0, 0, parameters.l.max * 3 );
    // // spotLight.map = new TextureLoader().load( url );

    // // spotLight.castShadow = true;

    // // spotLight.shadow.mapSize.width = 1024;
    // // spotLight.shadow.mapSize.height = 1024;

    // // spotLight.shadow.camera.near = 0.5;
    // // spotLight.shadow.camera.far = parameters.l.max * 6;
    // // spotLight.shadow.camera.fov = 100;

    const light = new DirectionalLight('white', 8);

    // move the light right, up, and towards us
    light.position.set(0, 0, parameters.l.max * 2);

    return light;

    // return spotLight
}

export { createLights }
