import * as THREE from 'three';

function createRod(cartesian){
    const verticies = new Float32Array([0,0,0, cartesian['x']* (this.parameters.l.max / this.parameters.l.value), cartesian['y']* (this.parameters.l.max / this.parameters.l.value), cartesian['z']* (this.parameters.l.max / this.parameters.l.value)])
    const LineMaterial = new THREE.LineBasicMaterial( { color: 'white' } );
    const lineGeometry = new THREE.BufferGeometry()
    const line = new THREE.Line( lineGeometry, LineMaterial );

    line.geometry.setAttribute('position', new THREE.BufferAttribute(verticies, 3))

    return [verticies, line]
}

export { createRod };
